--GENERATED ALWAYS--- MEANS WE CAN'T INSERT IN IT 

CREATE TABLE NEW_USER
(ID_NUM NUMERIC GENERATED ALWAYS AS IDENTITY,
USERPASS VARCHAR(10) NOT NULL);

INSERT INTO NEW_USER (USERPASS) VALUES ('P123');

SELECT * FROM NEW_USER ORDER BY ID_NUM ;

--THIS INSERT DOESNT WORK CZ THE IDENTITY IS GENERATED ALWAYS

INSERT INTO NEW_USER VALUES (12, 'P321');

-- GENERATED BY DEFAULT-- IF U INSERT IN IT NP OR IF U DIDNT IT'LL AUTOMATICALLY GENERATE A VALUE --BUT CANT BE NULL AND CAN'T INSERT NULL

CREATE TABLE NEW_USER1
(ID_NUM NUMERIC GENERATED BY DEFAULT AS IDENTITY,
USERPASS VARCHAR(10) NOT NULL);

-- GENERATED BY DEFAULT ON NULL-- IF U INSERT IN IT NP OR IF U DIDNT IT'LL AUTOMATICALLY GENERATE A VALUE --IT CAN BE NULL AND U CAN INSERT A NULL

CREATE TABLE NEW_USER2
(ID_NUM NUMERIC GENERATED BY DEFAULT ON NULL AS IDENTITY,
USERPASS VARCHAR(10) NOT NULL);

-- WE CAN CONTROL FROM WHAT NUM TO START AND BY HOW MUCH INCREMENT

CREATE TABLE NEW_USER2
(ID_NUM NUMERIC GENERATED BY DEFAULT AS IDENTITY START WITH 100 INCREMENT BY 10,
USERPASS VARCHAR(10) NOT NULL);

--DEFAULT SEQUANCE

CREATE SEQUENCE SEQ_USER_NUM;

INSERT INTO NEW_USER2 VALUES (SEQ_USER_NUM.NEXTVAL, 'P1234');

SELECT * FROM NEW_USER2 ORDER BY ID_NUM;

--
CREATE SEQUENCE SEQ_USER_NUM
START WITH 1000
INCREMENT 10
NOCACHE
CYCLE
MAXVALUE 1060;

--ALTERING A SEQUENCE ONLY (MAXVALUE)

ALTER SEQUENCE SEQ_USER_NUM
MAXVALUE 1000000000;
